import { gql } from "@apollo/client";

export const ALL_CHARACTERS = gql`
  query {
    allSpecies {
      species {
        name
        language
        designation
        homeworld {
          name
        }
        filmConnection {
          films {
            title
            director
            producers
            openingCrawl
            releaseDate
          }
        }
      }
    }
  }
`;
