import { gql } from "@apollo/client";
import { getCharactersByName } from "../../selectors/getCharactersByName";

export const ALL_CHARACTERS = gql`
  query {
    allPeople {
      people {
        id
        name
        gender
        birthYear
        homeworld {
          name
        }
        hairColor
        height
        filmConnection {
          films {
            title
            director
            producers
            openingCrawl
            releaseDate
          }
        }
      }
    }
  }
`;
