import React from "react";
import { useQuery } from "@apollo/client";
import People from "./People";
import { ALL_CHARACTERS } from "../../data/data";

export const CharacterList = () => {
  const { loading, error, data } = useQuery(ALL_CHARACTERS);

  if (loading) return <p>LOADING .. :(</p>;
  if (error) return <p>Error :(</p>;

  function renderCharacteres() {
    return data.allPeople.people.map((character) => {
      return (
        <People
          key={character.id}
          name={character.name}
          characterId={character.id}
        />
      );
    });
  }
  return (
    <>
      {loading ? (
        <p>LOADING ...</p>
      ) : (
        <div className="flex container mx-auto py-6">
          <div className="w-6/12">
            <li>{renderCharacteres()}</li>
          </div>
          <div className="w-6/12">Ditails</div>
        </div>
      )}
    </>
  );
};
