import { ALL_CHARACTERS } from "../../data/data";

export const getCharactersByName = (name) => {
  const validName = [ALL_CHARACTERS];

  if (!validName.includes(name)) {
    throw new Error(`Name "${name}" not found`);
  }

  return ALL_CHARACTERS.filter(
    (character) => character.ALL_CHARACTERS === name
  );
};
