import { gql } from "@apollo/client";

export const ALL_CHARACTERS = gql`
  query {
    allPeople {
      people {
        id
        name
      }
    }
  }
`;

export const GET_PERSON = gql`
  query {
    person(personID: 1) {
      name
      birthYear
      gender
      homeworld {
        name
      }
      hairColor
      height
      filmConnection {
        films {
          title
          director
          producers
          openingCrawl
          releaseDate
        }
      }
    }
  }
`;
